import React, { Component } from 'react';
import './Header.css';
import { Form, Nav, Navbar, Button } from "react-bootstrap";
import { NavLink } from 'react-router-dom'

import { setCookie, getCookie } from "../../services/Cookie";

class Header extends React.Component {
  constructor(props){
    super(props);

    this.state={
      login: false
        }
    }

    showLoginSignup() {
      return (
        <>
        <li class="nav-item">
          <NavLink
            to="/login"
            className="nav-link"
            activeStyle={{ fontWeight: 'bold' }}
          >
            Login
          </NavLink>
        </li>
          <li class="nav-item">
          <NavLink
              to="/signup"
              className="nav-link"
              activeStyle={{ fontWeight: 'bold' }}
            >
            Signup
          </NavLink>
        </li>
        </>
      )
    }

    showUserManagement() {
      const message = "Hello, " + getCookie('username')
      return(
        <li class="nav-item">
          <NavLink
            to="/"
            className="nav-link"
            activeStyle={{ fontWeight: 'bold' }}
          >
            {message}
          </NavLink>
        </li>
      )
    }

    render() {
        return (
            <div class="navbar"> 
                <ul class="navbar-left">
                  <li class="nav-item">
                      <NavLink
                          to="/"
                          className="nav-link"
                      >
                        Habit Rabbit
                      </NavLink>
                  </li>
                  </ul>
                <ul class="navbar-right">
                  <li class="nav-item">
                      <NavLink
                          to="/home"
                          className="nav-link"
                          activeStyle={{ fontWeight: 'bold' }}
                      >
                        Home
                      </NavLink>
                  </li>
                  <li class="nav-item">
                      <NavLink
                          to="/about"
                          className="nav-link"
                          activeStyle={{ fontWeight: 'bold' }}
                      >
                        About
                      </NavLink>
                  </li>
                  {!getCookie('login')?
                    this.showLoginSignup()
                    :
                    this.showUserManagement()
                  }
                </ul>
            </div>  
    );
  }
}

export default Header;
